import React, {useState} from 'react'
import { Card, Button } from 'react-bootstrap'
import {Link} from 'react-router-dom'
import fb from '../firebase'

const Product = ({product, uid}) => {

    const [user, setUser] = useState({});

    const addToCart = (e) => {
        e.preventDefault();

        const db = fb.firestore();

        let user;

        console.log(uid);

        db.collection('users').doc(uid).get()
        .then((doc) => {
            const data = doc.data();
            setUser(data);
            console.log(data);

        })
    }

    return (
        <>
            <Card>
                <div className="text-center py-3" >
                    <Card.Img variant="top" src={product.image} className="card-image my-3"/>
                </div>
                <Card.Body>
                    <Card.Title style={{height: "75px", overflow:"hidden"}} >
                       {product.name}
                    </Card.Title> 
                    <Card.Text>
                        Price: {product.price}
                    </Card.Text>
                    <Card.Text>
                        Rating: {product.rating} | {product.numReviews} user ratings
                    </Card.Text>
                    <Button className="btn btn-outline-secondary m-3" onClick={addToCart} > Add To Cart </Button>
                    <Link to={`/products/${product.uid}`}>
                        <Button className="btn btn-outline-secondary m-3 "> See Details </Button>
                    </Link>
                </Card.Body>
            </Card>
        </>
    )
}

export default Product
